class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(o){o.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(o){let n=o.data.comment;const m=t.createCommentDom(n);$(`#post-comments-${e}`).prepend(m),t.deleteComment($(" .delete-comment-button",m)),new ToggleLike($(" .toggle-like-button",m)),$(`#post-${e}-comments-form>input`).val(""),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}createCommentDom(e){return $(`<li id="comment-${e._id}">\n      <p>\n        <small>\n          <a class="delete-comment-button" href="/comments/destroy/${e._id}">X</a>\n        </small>\n        ${e.content}\n        <br />\n        <small> Comment from: ${e.user.name} </small>\n        <br />\n        <small>\n          <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n            0 Likes\n          </a>\n        </small>\n      </p>\n    </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}